<span class="line"><span style="color:#C792EA;">var</span><span style="color:#A6ACCD;"> FLAG_C </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">4</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;">// 0100</span></span>
