import{_ as o,c as a,o as t,ag as r}from"./chunks/framework.C86mscM5.js";const l="/fe-handbook/assets/eac9747548fc4cadb66c21aeca59eb7d.DJB_ZK7x.png",i="/fe-handbook/assets/e2f7bd678b3e4f2bbd6cd7b772c99dd0.CgvPGpjJ.png",b=JSON.parse('{"title":"开源许可证","description":"","frontmatter":{},"headers":[],"relativePath":"other/matrix/license.md","filePath":"other/matrix/license.md","lastUpdated":1744516180000}'),n={name:"other/matrix/license.md"};function h(c,e,s,u,p,f){return t(),a("div",null,e[0]||(e[0]=[r('<h1 id="开源许可证" tabindex="-1">开源许可证 <a class="header-anchor" href="#开源许可证" aria-label="Permalink to &quot;开源许可证&quot;">​</a></h1><blockquote><p>A software license tells others what they can and can&#39;t do with your source code. For your repository to truly be open source, you&#39;ll need to license it so that others are free to use, change, and distribute the software.</p></blockquote><blockquote><p>版权法默认禁止共享，也就是说，没有许可证的软件，就等同于保留版权，虽然开源了，用户只能看源码，不能用，一用就会侵犯版权。所以软件开源的话，必须明确地授予用户开源许可证。</p></blockquote><h2 id="一图胜千言" tabindex="-1">一图胜千言 <a class="header-anchor" href="#一图胜千言" aria-label="Permalink to &quot;一图胜千言&quot;">​</a></h2><p><img src="'+l+'" alt="在这里插入图片描述"></p><h2 id="宽松式许可证" tabindex="-1">宽松式许可证 <a class="header-anchor" href="#宽松式许可证" aria-label="Permalink to &quot;宽松式许可证&quot;">​</a></h2><blockquote><ul><li>没有使用限制</li><li>没有担保</li><li>披露要求（notice requirement）</li><li>用户可以修改代码后闭源</li><li>举例：<a href="https://zh.wikipedia.org/wiki/BSD%E8%AE%B8%E5%8F%AF%E8%AF%81" target="_blank" rel="nofollow noopener noreferrer">BSD</a>、<a href="https://zh.wikipedia.org/wiki/MIT%E8%A8%B1%E5%8F%AF%E8%AD%89" target="_blank" rel="nofollow noopener noreferrer">MIT</a>、Apache 2</li></ul></blockquote><h2 id="copyleft" tabindex="-1">Copyleft <a class="header-anchor" href="#copyleft" aria-label="Permalink to &quot;Copyleft&quot;">​</a></h2><blockquote><ul><li>如果分发二进制格式，必须提供源码</li><li>修改后的源码，必须与修改前保持许可证一致</li><li>不得在原始许可证以外，附加其他限制</li><li>修改后的 Copyleft 代码不得闭源</li><li>举例：Affero GPL (AGPL)、<a href="https://zh.wikipedia.org/wiki/GNU%E9%80%9A%E7%94%A8%E5%85%AC%E5%85%B1%E8%AE%B8%E5%8F%AF%E8%AF%81" target="_blank" rel="nofollow noopener noreferrer">GPL</a>、LGPL、Mozilla（MPL）</li></ul></blockquote><h2 id="gpl许可证" tabindex="-1">GPL许可证 <a class="header-anchor" href="#gpl许可证" aria-label="Permalink to &quot;GPL许可证&quot;">​</a></h2><blockquote><ul><li>自由软件基金会</li><li>允许任何人观看、修改，并散播程序软件里的原始程序码，唯一条件是如果你要发布修改后的版本就要连源代码一起公布</li></ul></blockquote><h2 id="开源软件如何挣钱" tabindex="-1">开源软件如何挣钱？ <a class="header-anchor" href="#开源软件如何挣钱" aria-label="Permalink to &quot;开源软件如何挣钱？&quot;">​</a></h2><ul><li>个人：靠捐赠，或项目火了以后大公司资助</li><li>企业：靠增值服务</li></ul><h2 id="github项目设置开源许可证" tabindex="-1">GitHub项目设置开源许可证 <a class="header-anchor" href="#github项目设置开源许可证" aria-label="Permalink to &quot;GitHub项目设置开源许可证&quot;">​</a></h2><p><a href="https://choosealicense.com/" target="_blank" rel="nofollow noopener noreferrer">how to choose</a><img src="'+i+'" alt="在这里插入图片描述"></p><h2 id="参考博客" tabindex="-1">参考博客 <a class="header-anchor" href="#参考博客" aria-label="Permalink to &quot;参考博客&quot;">​</a></h2><p><a href="http://www.ruanyifeng.com/blog/2017/10/open-source-license-tutorial.html" target="_blank" rel="nofollow noopener noreferrer">http://www.ruanyifeng.com/blog/2017/10/open-source-license-tutorial.html</a></p><p><a href="http://www.ruanyifeng.com/blog/2011/05/how_to_choose_free_software_licenses.html" target="_blank" rel="nofollow noopener noreferrer">http://www.ruanyifeng.com/blog/2011/05/how_to_choose_free_software_licenses.html</a></p>',18)]))}const _=o(n,[["render",h]]);export{b as __pageData,_ as default};
